import { logLevelForStatusCode, propagateStatusCode } from '@adobe/helix-shared-utils';
import { IndexConfig } from '@adobe/helix-shared-config';
import { fetchS3 } from './fetch-s3.js';
import { fetch } from './utils.js';

/**
 * Name of the indexing configuration, subject to be renamed.
 */
export const INDEX_CONFIGURATION_NAME = 'helix-query.yaml';

/**
 * Checks if the response is valid and returns an index config.
 *
 * @param {Logger} log logger
 * @param {Response} response content response from the storage
 * @param {string} key storage key (logging information)
 * @param {string} type type of storage (logging information
 * @returns {Promise<IndexConfig>} the index config or null
 */
async function handleResponse(log, response, key, type) {
  const text = await response.text();
  if (response.ok) {
    return new IndexConfig().withSource(text).init();
  }

  if (response.status === 404) {
    log.info(`No index configuration found in ${type} ${key}, ${text}`);
    return null;
  }

  log[logLevelForStatusCode(response.status)](`Invalid response (${response.status}) when fetching index configuration from ${type} ${key}`);
  const err = new Error(`Unable to fetch index configuration: ${response.status} ${text}`);
  err.status = propagateStatusCode(response.status);
  throw err;
}

/**
 * Retrieves the indexing configuration from the underlying storage and stores it in the context as
 * indexConfig.
 *
 * @param {AdminContext} ctx the context
 * @param {LookupOptions} opts lookup options
 * @param {boolean} optional if {@code true}, no error is thrown if the config can't be retrieved
 * @returns {Promise<IndexConfig>} the index config
 */
export async function fetchIndex(ctx, opts, optional) {
  const { attributes, log } = ctx;
  const {
    owner, repo, ref, branch,
  } = opts;

  if (attributes.indexConfig) {
    return attributes.indexConfig;
  }

  const key = `${owner}/${repo}/${branch || ref}/${INDEX_CONFIGURATION_NAME}`;
  let response = await fetchS3(ctx, 'helix-code-bus', key);
  attributes.indexConfig = await handleResponse(log, response, key, 'code-bus');
  if (attributes.indexConfig) {
    return attributes.indexConfig;
  }

  // try loading from github for non helix-3 repos
  const headers = {};
  if (ctx.githubToken) {
    headers.authorization = `token ${ctx.githubToken}`;
  }
  response = await fetch(`https://raw.githubusercontent.com/${key}`, {
    cache: 'no-store',
    headers,
  });
  attributes.indexConfig = await handleResponse(log, response, key, 'github');

  if (attributes.indexConfig || optional) {
    return attributes.indexConfig;
  }

  throw Error(`no index configuration for ${owner}/${repo}/${ref}`);
}
