import { logLevelForStatusCode, propagateStatusCode } from '@adobe/helix-shared-utils';
import { SitemapConfig } from '@adobe/helix-shared-config';
import { fetchS3 } from './fetch-s3.js';

/**
 * Checks if the response is valid and returns a sitemap config.
 *
 * @param {Logger} log logger
 * @param {Response} response content response from the storage
 * @param {string} key storage key (logging information)
 * @param {string} type type of storage (logging information
 * @returns {Promise<SitemapConfig>} the sitemap config or null
 */
async function handleResponse(log, response, key, type) {
  const text = await response.text();
  if (response.ok) {
    const cfg = await new SitemapConfig().withSource(text).init();
    cfg.sourceType = type;
    return cfg;
  }

  if (response.status === 404) {
    log.info(`No helix-sitemap.yaml found in ${type} ${key}, ${text}`);
    return null;
  }

  log[logLevelForStatusCode(response.status)](
    `Invalid response (${response.status}) when fetching sitemap configuration from ${type} ${key}`,
  );
  const err = new Error(`Unable to fetch sitemap configuration: ${response.status} ${text}`);
  err.status = propagateStatusCode(response.status);
  throw err;
}

/**
 * Retrieves the sitemap from the underlying storage and stores it in the context as sitemapConfig.
 * @param {AdminContext} ctx the context
 * @param {LookupOptions} opts lookup options
 * @param {boolean} optional if {@code true}, no error is thrown if the config can't be retrieved
 * @returns {Promise<SitemapConfig>} the sitemap configuration
 */
export async function fetchSitemap(ctx, opts, optional) {
  const { attributes, log } = ctx;
  const { owner, repo, ref } = opts;

  if (attributes.sitemapConfig) {
    return attributes.sitemapConfig;
  }

  const key = `${owner}/${repo}/${ref}/helix-sitemap.yaml`;
  const response = await fetchS3(ctx, 'helix-code-bus', key);
  attributes.sitemapConfig = await handleResponse(log, response, key, 'code-bus');
  if (attributes.sitemapConfig || optional) {
    return attributes.sitemapConfig;
  }
  throw Error(`no sitemap configuration for ${key}`);
}
